const LAYOUTS = require('../constant/layouts')
/*
 [
 'CompactBoxTree',
 'Dendrogram',
 'IndentedTree',
 'LayeredTidyTree',
 'LogicalMindmap',
 ]
 */
const DIRECTIONS = [
  'LR',
  'RL',
  'TB',
  'BT',
  'H',
  'V'
]

module.exports = (layout) => {
  let directions
  if (layout === LAYOUTS[2]) {
    directions = [
      DIRECTIONS[0],
      DIRECTIONS[1],
      DIRECTIONS[4]
    ]
  } else {
    directions = DIRECTIONS
  }
  return `${
    directions
      .map(direction => `<option value="${direction}">${direction}</option>`)
      .join('')
    }`
}
