const LAYOUTS = require('../constant/layouts')
/*
 [
 'CompactBoxTree',
 'Dendrogram',
 'IndentedTree',
 'LayeredTidyTree',
 'LogicalMindmap',
 ]
 */

function formItemsByMeta (arr) {
  return `${arr.map(item => {
    const props = Object.assign({
      min: 10,
      max: 200,
      value: 20
    }, item)
    return `<label class="form-label">${props.name}</label>
<input name="${props.name}" class="form-item" type="range"
 min="${props.min}" max="${props.max}" value="${props.value}"/>`
  }
  ).join('')}`
}

module.exports = (layout) => {
  if (layout === LAYOUTS[0] || layout === LAYOUTS[4]) {
    return formItemsByMeta([
      {
        name: 'hgap'
      },
      {
        name: 'vgap'
      }
    ])
  }
  if (layout === LAYOUTS[1]) {
    return formItemsByMeta([
      {
        name: 'nodeSep'
      },
      {
        name: 'nodeSize'
      },
      {
        name: 'rankSep',
        value: 200
      },
      {
        name: 'subTreeSep'
      }
    ])
  }
  if (layout === LAYOUTS[2]) {
    return formItemsByMeta([
      {
        name: 'indent'
      },
      {
        name: 'vgap',
        min: 0
      }
    ])
  }
  if (layout === LAYOUTS[3]) {
    return formItemsByMeta([
      {
        name: 'nodeSep'
      },
      {
        name: 'nodeSize'
      },
      {
        name: 'rankSep',
        value: 200
      }
    ])
  }
}
