const TreeLayout = require('./tree-layout-base')
const dendrogram = require('../algorithm/dendrogram')
const doTreeLayout = require('./do-tree-layout')

class DendrogramLayout extends TreeLayout {
  doLayout () {
    const me = this
    const root = me.root
    const options = me.options
    root.width = 0
    doTreeLayout(root, options, dendrogram)
    return root
  }
}

module.exports = DendrogramLayout
